//printAll function that pushes variables through and prints them
function printAll() {
    //Checking variable to ensure a blank message is not sent
    if (adventurers_level >= 2) {
        //Assign the function to a variable and sending it through to discord
        var oilstring = oilCreation(adventurers_level);
        client.channels.cache.get(channel).send(oilstring);
        //Add to messages variable for clear function
        messages++;
    }

    if (arcane_level > 0) {
        var scrollstring = scrollCreation(arcane_level);
        client.channels.cache.get(channel).send(scrollstring);
        messages++;
    }

    if (blacksmith_level > 0) {
        var runestring = runeCreation(blacksmith_level);
        //Used to split messages if they are too long for discords character limit
        if(runestring.length > 2000) {
            runestring2 = runestring.substring(0, 2000);
            runestring = runestring.slice(2001, runestring.length);
            client.channels.cache.get(channel).send(runestring2);
            client.channels.cache.get(channel).send(runestring);
            messages += 2;
        }
        else
            client.channels.cache.get(channel).send(runestring);
            messages++;
    }

    if (arcane_level > 0) {
        var gemstring = gemCreation(arcane_level);
        client.channels.cache.get(channel).send(gemstring);
        messages++;
    }

    if (potion_level > 0) {
        var potionstring = potionCreation(potion_level);
        client.channels.cache.get(channel).send(potionstring);
        messages++;
    }

    if (blacksmith_level > 0) {
        var weaponstring = weaponArmorCreation(blacksmith_level);
        if(weaponstring.length > 2000) {
            weaponstring2 = weaponstring.substring(0, 2000);
            weaponstring = weaponstring.slice(2001, weaponstring.length);
            client.channels.cache.get(channel).send(weaponstring2);
            client.channels.cache.get(channel).send(weaponstring);
            messages += 2;
        }
        else
            client.channels.cache.get(channel).send(weaponstring);
            messages++;
    }
}

