function potionCreation(potion_level) {
    //set up final string to be returned at end of function
    finalpotionstring = "";

    finalpotionstring += ("()()Potions()()" + "\n");

    //array of arrays for potion levels and strengths. First three are for positive effects, second three are for negative effects
    potion_array = [
        [ "Add 1d6 to your next successful attack", "Regain 1 hit die worth of health", "Regain 1 spell slot, if no spell slots avaliable, cast 1 cantrip", "+2 towards persuasion rolls", "+1 ac", "+10 movement speed", "A false body double is created and the user becomes invisible for 5min", "+2 to hit", "Remove all thirst and hunger from the user for 1 day", "The user can choose to change 1 aspect of their personal apperance", "For 1hr you can speek to plants and animals", "See perfectly in the dark for 1hr" ],
        [ "5ft of flying speed for 5min", "Breath underwater for 20min", "Turn invisible for 10min", "Teleport to a location within 20ft of the user", "The next time you would go down in combat instead remain at 1hp", "Create a tunnel 5ft in diameter and 10ft deep with a ladder in front of the user", "Gain 1d6 temporary health", "You may exchange any amount of gold for exp at a rate of one gp per 2exp", "When dealing a finishing blow on an enemy regain 1 health per damage delt" ],
        [ "Everyone believes the next 1 lie you tell", "Become ethereal for 10min", "Crits happen on rolls 1 less than usual", "Duplicate 1 treasure you have", "Gain 1d6 temporary health, when this health is removed deal 1d6 damage to all enemies in a 10ft area around you" ],
        [ "1 random body feature is changed", "For the next 5hr you can only taste and smell gold", "You give 1d4 gold to the next person you see", "Your next successful attack deals 1d6 damage less", "-10 movement speed", "Insects swarm from the users mouth whenever they lie for the next 10hr", "The user falls in love with the next 1 sentient creature they see", "Take 2d6 damage", "For 10min you take the form of a zombie" ],
        [ "For the users next 1 turn they can't act on their own, they must follow any orders given to them", "Teleport the player to a random area within 20ft", "-2ac", "Take an additional 1d4 damage from ranged attacks for 1 turn", "Take an additonal 1d4 damage from melee attacks for 1 turn", "You beleive all lies told to you for 1hr", "You become mute for 1hr" ],
        [ "Unable to use magic for 10min", "Destroy 1 item the user owns", "You are blinded in sunlight for 10min", "The user can not hit critical strikes for 10min" ],
    ];

    for (var x = 0; x < (potion_level + 2); x++)
    {
        var level = 1;
        var gold = 0;

        //set up potion level, chosen randomly weighted towards lower levels
        var randlevel = randomIntFromInterval(1, 6);
        switch (randlevel) {
            case 1:
            case 2:
            case 3:
                level = 1;
                break;

            case 4:
            case 5:
                level = 2;
                break;

            case 6:
                level = 3;
                break;
        }

        //check for certain characters in the string, if included, multiply them by the level
        var includes1 = false;
        var includes2 = false;
        var includes5 = false;

        var strone = (1 * level);
        var strtwo = (2 * level);
        var five = (5 * level);

        var one = (strone);
        var two = (strtwo);

        for (var i = 0; i < potion_array.length - 1; i++)
        {
            for (var j = 0; j < potion_array[i].length - 1; j++)
            {
                includes1 = potion_array[i][j].includes('1');
                includes2 = potion_array[i][j].includes('2');
                includes5 = potion_array[i][j].includes('5');
                if (includes1) potion_array[i][j] = potion_array[i][j].replace('1', one);
                else if (includes2) potion_array[i][j] = potion_array[i][j].replace('2', two);
                else if (includes5) potion_array[i][j] = potion_array[i][j].replace("5", five);
            }
        }

        //set up the positive chance of the potion. Weighted towards lower positive effects
        var poschance = randomIntFromInterval(1, 6);
        var pos = 0;
        switch (poschance) {
            case 1:
            case 2:
            case 3:
                pos = 0;
                gold += 1;
                break;

            case 4:
            case 5:
                pos = 1;
                gold += 2;
                break;

            case 6:
                pos = 2;
                gold += 3;
                break;
        }

        var negchance = randomIntFromInterval(1, 6);
        var neg = 0;
        var neggold = 0;
        switch (negchance) {
            case 1:
            case 2:
            case 3:
                neg = 3;
                neggold = 0;
                break;

            case 4:
            case 5:
                neg = 4;
                neggold = 1;
                break;

            case 6:
                neg = 5;
                neggold = 2;
                break;
        }

        //Set up random chance for the power of a potion. Higher power means the potion has more positive and negative effects
        var randend = randomIntFromInterval(0, 9);
        var randpower = randomIntFromInterval(1, 6);
        switch (randpower) {
            case 1:
            case 2:
            case 3:
                //choose a random positive effect, calculate gold, and add them to the final string
                var positive = randomIntFromInterval(0, potion_array[pos].length - 1);
                gold *= level * 2;
                finalpotionstring += ("" + (gold + 10) + "gp: ");
                finalpotionstring += (potion_array[pos][positive] + "\n");
                break;

            case 4:
            case 5:
                //same as above but with more positive effects and a negative effect
                positive = randomIntFromInterval(0, potion_array[pos].length - 1);
                var positive2 = randomIntFromInterval(0, potion_array[pos].length - 1);
                var negative = randomIntFromInterval(0, potion_array[neg].length - 1);
                gold = (gold + (2 - neggold)) * level;
                finalpotionstring += ("" + gold + randend + "gp: ");
                finalpotionstring += (potion_array[pos][positive] + ", ");
                finalpotionstring += (potion_array[pos][positive2] + ", ");
                finalpotionstring += ("however, ");
                finalpotionstring += (potion_array[neg][negative] + "\n");
                break;

            case 6:
                //Three positive effects, one negative effect
                positive = randomIntFromInterval(0, potion_array[pos].length - 1);
                positive2 = randomIntFromInterval(0, potion_array[pos].length - 1);
                var positive3 = randomIntFromInterval(0, potion_array[pos].length - 1);
                negative = randomIntFromInterval(0, potion_array[neg].length - 1);
                var negative2 = randomIntFromInterval(0, potion_array[neg].length - 1);
                gold = (gold + (3 - neggold)) * level;
                finalpotionstring += ("" + gold + randend + "gp: ");
                finalpotionstring += (potion_array[pos][positive] + ", ");
                finalpotionstring += (potion_array[pos][positive2] + ", ");
                finalpotionstring += (potion_array[pos][positive3] + ", ");
                finalpotionstring += ("however, ");
                finalpotionstring += (potion_array[neg][negative] + ", ");
                finalpotionstring += (potion_array[neg][negative2] + "\n");
                break;
        }

    }

    finalpotionstring += "\n \n .";

    return finalpotionstring;
}

