function oilCreation(adventurers_level) {
    //set up final string to be returned at end of function
    var finaloilstring = "";

    //Arrays for different oils
    const damage = [ " 1d4", " 1d6", " 1d10", " 2d6", " 2d8" ];
    const element = [ " Poison damage", " Fire damage", " Cold damage", " Lightning damage", " Acid damage", " Necrotic damage", " Radiant damage" ];
    const time = [ " for 1 turn/2 arrows", " for 1 week/20 arrows", " for 1 month/60 arrows arrows", " Forever/any number of arrows" ];

    var oil_dmg = 0;
    var oil_elem = 0;
    var oil_time = 0;
    var gold = 0;

    finaloilstring += "(/)Oils(/) \n";

    //Lots of bad code here. Will probably redo this at a later date
    if (adventurers_level >= 2) {
        for (var i = 0; i < (adventurers_level * 3); i++)
        {
            oil_dmg = randomIntFromInterval(0, 2);
            oil_elem = randomIntFromInterval(0, 4);
            oil_time = randomIntFromInterval(0, 2);

            if (oil_dmg == 0) gold = 15;
            else if (oil_dmg == 1) gold = 25;
            else gold = 40;

            if (oil_elem == 0) gold *= 3;
            else gold *= 10;

            if (oil_time == 0) gold /= 5;


            finaloilstring += (gold + "gp: Adds" + damage[oil_dmg] + element[oil_elem] + time[oil_time] + "\n");

        }
    }

    finaloilstring += "\n";

    if (adventurers_level >= 3) {
        for (var i = 0; i < (adventurers_level * 2); i++)
        {
            oil_dmg = randomIntFromInterval(1, 3);
            oil_elem = randomIntFromInterval(1, 5);
            oil_time = randomIntFromInterval(0, 3);

            if (oil_dmg == 1) gold = 25;
            else if (oil_dmg == 2) gold = 40;
            else gold = 50;

            if (oil_elem == 5) gold *= 12;
            else gold *= 10;

            if (oil_time == 0) gold /= 5;
            else if (oil_time == 2) gold *= 5;

            finaloilstring += (gold + "gp: Adds" + damage[oil_dmg] + element[oil_elem] + time[oil_time] + "\n");

        }
    }

    finaloilstring += "\n";

    if (adventurers_level >= 4) {
        for (var i = 0; i < (adventurers_level * 1); i++)
        {
            oil_dmg = randomIntFromInterval(2, 4);
            oil_elem = randomIntFromInterval(1, 6);
            oil_time = randomIntFromInterval(0, 4);

            if (oil_dmg == 2) gold = 40;
            else if (oil_dmg == 3) gold = 50;
            else gold = 65;

            if (oil_elem == 5) gold *= 12;
            else if (oil_elem == 6) gold *= 15;
            else gold *= 10;

            if (oil_time == 0) gold /= 5;
            else if (oil_time == 2) gold *= 5;
            else if (oil_time == 3) gold *= 10;

            finaloilstring += (gold + "gp: Adds" + damage[oil_dmg] + element[oil_elem] + time[oil_time] + "\n");

        }
    }

    finaloilstring += "\n \n .";

    return finaloilstring;
}

function randomIntFromInterval(min, max) { // min and max included 
    return Math.floor(Math.random() * (max - min + 1) + min)
}

